import React, { useState, useContext } from "react";
import { Redirect } from "react-router-dom";
import ItemContext from "./itemContext";

function AddItemForm() {
    const items = useContext(ItemContext);
    const [itemType, setItemType] = useState('');
    const [formData, setFormData] = useState({
        id: '',
        name: '',
        description: '',
        recipe: '',
        serve: ''
    });

    const handleSubmit = (e) => {
        e.preventDefault();
        const addId = formData.name.replace(' ', '-');

        const newItem = ({
            id: addId,
            ...formData
        })


        if (itemType === 'snack') {
            items['drink'] = { ...items['drink'], newItem }

        } else if (itemType === 'drink') {
            items['drink'] = { ...items['drink'], newItem }
        }

        <Redirect to={`/${itemType}`} />;

    }

    const onInputChange = (e) => {
        const { name, value } = e.target.value;
        setFormData({
            ...formData,
            [name]: value
        });
    }

    const handleRadioChange = (e) => {
        setItemType(e.target.value);
    }

    return (
        <div id="add-item-form">
            <form onSubmit={handleSubmit}>
                <fieldset>
                    <legend>Select the type of item you are adding:</legend>
                    <label htmlFor=""></label>
                    <input id="item-drink"
                        type="radio"
                        value="drink"
                        checked={itemType === 'drink'}
                        onChange={handleRadioChange}>Drink</input>

                    <label htmlFor=""></label>
                    <input id="item-snack"
                        type="radio"
                        value="snack"
                        checked={itemType === 'snack'}
                        onChange={handleRadioChange}>Snack</input>
                </fieldset>
                <input id="item-name"
                    type="text"
                    value={formData.name}
                    onChange={onInputChange}>Name</input>
                <input id="description"
                    type="text"
                    value={formData.description}
                    onChange={onInputChange}>Description</input>
                <input id="recipe"
                    type="text"
                    value={formData.recipe}
                    onChange={onInputChange}>Recipe</input>
                <input id="serve"
                    type="text"
                    value={formData.serve}
                    onChange={onInputChange}>Serve</input>
                <button type="submit">Add Item</button>
            </form >
        </div >
    );
}

export default AddItemForm;
