import React, { useEffect, useState } from "react";
import { Redirect, useParams } from "react-router-dom";
import { Card, CardBody, CardTitle, CardText } from "reactstrap";

function FoodItem({ snacks, drinks, cantFind }) {
  const { id } = useParams();
  const { itemType } = useParams();
  const [items, setItems] = useState([]);

  useEffect(() => {
    if (itemType === 'snacks') {
      setItems(snacks);
    }

    if (itemType === 'drinks') {
      setItems(drinks);
    }
  }, [itemType, items]);


  let food = items.find(i => i.id === id);
  if (!food) return <Redirect to={cantFind} />;

  return (
    <section>
      <Card>
        <CardBody>
          <CardTitle className="font-weight-bold text-center">
            {food.name}
          </CardTitle>
          <CardText className="font-italic">{food.description}</CardText>
          <p>
            <b>Recipe:</b> {food.recipe}
          </p>
          <p>
            <b>Serve:</b> {food.serve}
          </p>
        </CardBody>
      </Card>
    </section>
  );
}

export default FoodItem;
