import React, { useState, useContext } from "react";
import { Redirect } from "react-router-dom";
import ItemContext from "./itemContext";
import './AddItemForm.css';

function AddItemForm() {
    const { items, setItems } = useContext(ItemContext);
    const [itemType, setItemType] = useState('');
    const [formData, setFormData] = useState({
        id: '',
        name: '',
        description: '',
        recipe: '',
        serve: ''
    });

    const handleSubmit = (e) => {
        e.preventDefault();
        const addId = formData.name.replace(' ', '-');

        const newItem = ({
            id: addId,
            ...formData
        });
        setItems(prevItems => ({
            ...prevItems,
            [itemType]: [...AddItemForm(prevItems[itemType] || []), newItem]
        }));

        <Redirect to={`/${itemType}`} />;

    }

    const onInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    }

    const handleRadioChange = (e) => {
        setItemType(e.target.value);
    }

    return (
        <div id="add-item-form">
            <form onSubmit={handleSubmit}>
                <fieldset>
                    <legend>Select the type of item you are adding:</legend>
                    <label htmlFor="item-drink">Drink</label>
                    <input id="item-drink"
                        type="radio"
                        value="drink"
                        checked={itemType === 'drink'}
                        onChange={handleRadioChange} />

                    <label htmlFor="item-snack">Snack</label>
                    <input id="item-snack"
                        type="radio"
                        value="snack"
                        checked={itemType === 'snack'}
                        onChange={handleRadioChange} />
                </fieldset>

                <label htmlFor="item-name">Name: </label>
                <input id="item-name"
                    type="text"
                    value={formData.name}
                    onChange={onInputChange} />

                <label htmlFor="description">Description: </label>
                <input id="description"
                    type="text"
                    value={formData.description}
                    onChange={onInputChange} />

                <label htmlFor="recipe">Recipe: </label>
                <input id="recipe"
                    type="text"
                    value={formData.recipe}
                    onChange={onInputChange} />

                <label htmlFor="serve">Serve: </label>
                <input id="serve"
                    type="text"
                    value={formData.serve}
                    onChange={onInputChange} />
                <button type="submit">Add Item</button>
            </form >
        </div >
    );
}

export default AddItemForm;
